mapper.select:
select top ${pageSize}
from ( select row_number() over(order by p.point_amout desc) as rownumber,p.*,u.stu_no,u.real_name from jt_point p left join jt_user_info u on p.uid=u.uid where 1=1
and u.stu_no like '%'+#{stuNo}+'%'
and u.real_name like '%'+#{stuName}+'%'
) A where rownumber > #{offset}
