mapper.select:
select top 1

from jt_student_info
where stu_no = #{stuNo}

select top ${pageSize}
from ( select row_number() over(order by create_time) as rownumber,* from jt_student_info where 1=1
and stu_no like '%'+#{stuNo}+'%'
and id_no like '%'+#{idNo}+'%'
and real_name like '%'+#{stuName}+'%'
and phone like '%'+#{phone}+'%'
) A where rownumber > #{offset}
